mapper.select:
SELECT id, userId, tag, title, content, imagePath, price, createdAt, updatedAt FROM post
WHERE tag = #{tag}
ORDER BY id DESC

mapper.update:
UPDATE `post` SET tag = #{tag}, title = #{title}, content = #{content},
imagePath =#{imagePath},
price = #{price}, updatedAt = now() WHERE id = #{postId} AND userId = #{userId};
